-- Auto-generated by Matugen
-- VSCode/Catppuccin-inspired color scheme with matugen integration

-- Catppuccin-inspired fixed colors for better syntax highlighting
local catppuccin = {
  red = '#E88594',
  green = '#A3D393',
  yellow = '#E9CE9D',
  blue = '#81C2E1',
  magenta = '#C3A0F3',
  comment = '#6c6c6c',
}

-- Base16 colors setup (VSCode-compatible, no plugin dependency)
local base16 = {
  base00 = '{{colors.surface.default.hex}}',           -- Background
  base01 = '{{colors.surface_container_low.default.hex}}',  -- Lighter background (status bars, line number)
  base02 = '{{colors.surface_container_high.default.hex}}', -- Selection background
  base03 = '{{colors.outline.default.hex}}',           -- Comments, invisibles, line highlighting
  base04 = '{{colors.on_surface_variant.default.hex}}', -- Dark foreground (status bars)
  base05 = '{{colors.on_surface.default.hex}}',        -- Default foreground
  base06 = '{{colors.on_surface.default.hex}}',        -- Light foreground
  base07 = '{{colors.surface_bright.default.hex}}',    -- Light background
  base08 = catppuccin.red,                              -- Variables, XML tags, markup link text
  base09 = catppuccin.yellow,                           -- Integers, booleans, constants
  base0A = catppuccin.yellow,                           -- Classes, markup bold
  base0B = catppuccin.green,                            -- Strings, markup code
  base0C = catppuccin.blue,                             -- Support, regular expressions
  base0D = '{{colors.secondary.default.hex}}',         -- Functions, methods (matugen)
  base0E = '{{colors.primary.default.hex}}',           -- Keywords, storage (matugen)
  base0F = catppuccin.magenta,                          -- Deprecated, embedded
}

-- Apply base16 colors directly (works in VSCode Neovim)
vim.api.nvim_set_hl(0, 'Normal', { fg = base16.base05, bg = base16.base00 })
vim.api.nvim_set_hl(0, 'NormalFloat', { fg = base16.base05, bg = base16.base01 })
vim.api.nvim_set_hl(0, 'NormalNC', { fg = base16.base05, bg = base16.base00 })


-- Additional highlight customizations for better readability

-- Helper function to set multiple highlight groups at once
local function set_hl_multiple(groups, value)
  for _, v in pairs(groups) do
    vim.api.nvim_set_hl(0, v, value)
  end
end

-- UI Elements
vim.api.nvim_set_hl(0, 'Visual', {
  bg = '{{colors.primary_container.default.hex}}',
  fg = nil,
})

vim.api.nvim_set_hl(0, 'CursorLine', {
  bg = '{{colors.surface_container_high.default.hex}}',
})

vim.api.nvim_set_hl(0, 'LineNr', {
  fg = '{{colors.outline.default.hex}}',
})

vim.api.nvim_set_hl(0, 'CursorLineNr', {
  fg = '{{colors.primary.default.hex}}',
  bold = true,
})

-- Comments (italic and muted)
set_hl_multiple({ 'Comment', '@comment' }, {
  fg = catppuccin.comment,
  italic = true,
})

-- Keywords (primary matugen color)
set_hl_multiple({ 'Keyword', '@keyword', '@keyword.control', '@keyword.function', 'Conditional', 'Repeat' }, {
  fg = '{{colors.primary.default.hex}}',
})

-- Functions (secondary matugen color)
set_hl_multiple({ 'Function', '@function', '@function.call', '@method', '@method.call' }, {
  fg = '{{colors.secondary.default.hex}}',
})

-- Strings (green)
set_hl_multiple({ 'String', '@string', '@string.escape' }, {
  fg = catppuccin.green,
})

-- Numbers & Constants (yellow/orange)
set_hl_multiple({ 'Number', '@number', 'Boolean', '@boolean', 'Constant', '@constant' }, {
  fg = catppuccin.yellow,
})

-- Types & Classes (blue)
set_hl_multiple({ 'Type', '@type', '@type.builtin', 'Structure', 'StorageClass' }, {
  fg = catppuccin.blue,
})

-- Variables (default text color)
set_hl_multiple({ 'Identifier', '@variable', '@parameter' }, {
  fg = '{{colors.on_surface.default.hex}}',
})

-- Special/Magic variables (red)
set_hl_multiple({ '@variable.builtin', 'Special' }, {
  fg = catppuccin.red,
  italic = true,
})

-- Operators
set_hl_multiple({ 'Operator', '@operator' }, {
  fg = '{{colors.on_surface.default.hex}}',
})

-- Punctuation
set_hl_multiple({ 'Delimiter', '@punctuation.delimiter', '@punctuation.bracket' }, {
  fg = '{{colors.on_surface_variant.default.hex}}',
})

-- Tags (HTML/XML)
set_hl_multiple({ 'Tag', '@tag', '@tag.delimiter' }, {
  fg = '{{colors.primary.default.hex}}',
})

-- Attributes
set_hl_multiple({ '@tag.attribute' }, {
  fg = '{{colors.secondary.default.hex}}',
  italic = true,
})

-- Errors & Warnings
vim.api.nvim_set_hl(0, 'Error', {
  fg = catppuccin.red,
  bold = true,
})

vim.api.nvim_set_hl(0, 'DiagnosticError', {
  fg = catppuccin.red,
})

vim.api.nvim_set_hl(0, 'DiagnosticWarn', {
  fg = catppuccin.yellow,
})

vim.api.nvim_set_hl(0, 'DiagnosticInfo', {
  fg = catppuccin.blue,
})

vim.api.nvim_set_hl(0, 'DiagnosticHint', {
  fg = '{{colors.on_surface_variant.default.hex}}',
})

-- Statusline & UI
vim.api.nvim_set_hl(0, 'StatusLine', {
  bg = '{{colors.surface_container.default.hex}}',
  fg = '{{colors.on_surface.default.hex}}',
})

vim.api.nvim_set_hl(0, 'MsgArea', {
  bg = '{{colors.surface.default.hex}}',
  fg = '{{colors.on_surface.default.hex}}',
})

-- Pmenu (completion menu)
vim.api.nvim_set_hl(0, 'Pmenu', {
  bg = '{{colors.surface_container_high.default.hex}}',
  fg = '{{colors.on_surface.default.hex}}',
})

vim.api.nvim_set_hl(0, 'PmenuSel', {
  bg = '{{colors.primary.default.hex}}',
  fg = '{{colors.on_primary.default.hex}}',
})

-- Search
vim.api.nvim_set_hl(0, 'Search', {
  bg = '{{colors.secondary_container.default.hex}}',
  fg = '{{colors.on_secondary_container.default.hex}}',
})

